package 연산자문제;

import java.util.*;

public class 연습문제_14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b;
		Scanner s = new Scanner(System.in);
		System.out.println("첫째수:");
		a = s.nextInt();
		System.out.println("둘째수:");
		b = s.nextInt();
		System.out.println("나머지:"+(a%b));

	}

}
