package 연산자응용문제;

public class 응용문제02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	     int i = 0; 
	     System.out.println("첫번째 결과 : " + i++ * 2); 
	     System.out.println("두번째 결과 : " + i);
	     //첫번째 결과 : 0
	     //두번쨰 결과 : 1
	     
	   //  int i = 0; 
	     System.out.println("첫번째 결과 : " + ++i * 2); 
	     System.out.println("두번째 결과 : " + i);
	     //첫번째 결과 : 2
	     //두번쨰 결과 : 1
	}

}
