package 연산자응용문제;

public class 응용문제04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	     int a = 3; 
	     a = a + 1;  // a+=1;
	     System.out.println(a); 
	     a += 1; 
	     System.out.println(a);
	     a -= 1; 
	     System.out.println(a); 
	     a *= 2;
	     System.out.println(a); 
	     a /= 2; 
	     System.out.println(a); 
	     a %= 2; 
	     System.out.println(a);

	     // 4
	     // 5
	     // 4
	     // 8
	     // 4
	     // 0
	}

}
